from django.contrib.auth import authenticatefrom django.contrib.auth.models import Userfrom rest_framework import serializersfrom django.utils.translation import ugettext_lazy as _class LoginSerializer(serializers.Serializer):    username = serializers.CharField(max_length=255)    password = serializers.CharField(        label=_('Password'),        style={'input_type': 'password'},        trim_whitespace=False,        max_length=255,        write_only=True    )    def validate(self, data):        username = data.get('username', None)        password = data.get('password', None)        user = authenticate(username=username, password=password, request=self.context.get('request'))        if not user:            raise serializers.ValidationError(                _('User not found'),                code='authorization'            )        data['user'] = user        return dataclass UserSerializer(serializers.ModelSerializer):    username = serializers.ModelField('username')    class Meta:        model = User        fields = ('id', 'first_name', 'last_name', 'username')